import React, { Component } from 'react';
import TodoList from './TodoList';
import AddTask from './AddTask';
import { Badge } from 'reactstrap';
class TaskList extends Component{
    constructor(props){
        super(props)
        this.state = {
            tasks: [],
            showAddForm: false,
        }
    }

    setStatus = () => {
        this.setState({
            showAddForm: true
        })
    }
    closeForm = () => {
        this.setState({
            showAddForm: false
        })
    }
    addTask = (name) => {
        this.state.tasks.push(name)
        this.forceUpdate()
    }

    render(){
        if (this.state.showAddForm === true) {
            return (
                <AddTask addTask={this.addTask} closeForm={this.closeForm} />
            )
        } else{
        return(
            <div className="container">
                <h1>To do App designed by     <Badge color="secondary">Mountain</Badge></h1>
                <br />
                <br />
                <h2>List Task</h2>
                <table className="table table-striped">
                <thead>
                    <tr>
                        <th>Name of task</th>
                    </tr>
                </thead>    
                    <tbody>
                        {
                            this.state.tasks.map((name,index) =>
                             <TodoList name={name}/>
                            )
                        }
                    </tbody>
                </table>
                <button type="submit" style={{marginRight: 5+ 'px'}} className="btn btn-default" onClick={this.setStatus}>Add Item</button>
            </div>
        )
        }
    }
}

export default TaskList;
