import React, { Component } from 'react';
import TodoList from './TodoList';
import AddTask from './AddTask';
import { Badge } from 'reactstrap';
import { Table } from 'reactstrap';
class TaskList extends Component{
    constructor(props){
        super(props)
        this.state = {
            tasks: [
            {detail:'Go club', time:'22/3/2021 9am', complete: 'false'},
            {detail:'Go shopping', time:'22/3/2021 10am', complete: 'false'},
            {detail:'Go home', time:'22/3/2021 11am', complete: 'false'}
            ],
            showAddForm: false,
        }
    }

    setStatus = () => {
        this.setState({
            showAddForm: true
        })
    }
    closeForm = () => {
        this.setState({
            showAddForm: false
        })
    }
    addTask = (name,time) => {
        let infor={detail:{name},time:{time},complete:'false'};  
        //let infor={detail:'nothing',time:'none',complete:'false'};        
        this.state.tasks.push(infor);
        console.log(this.state.tasks);
        //debugger;   
        this.forceUpdate()
    }

    render(){
        if (this.state.showAddForm === true) {
            return (
                <AddTask addTask={this.addTask} closeForm={this.closeForm} />
            )
        } else{
        return(
            <div className="container">
                <h1>To do App designed by <Badge color="success">Mountain</Badge></h1>
                <br />
                <br />
                <h2>List Task</h2>
                <Table hover className="table">
                <thead>
                    <tr>
                        <th>STT</th>
                        <th>Name of task</th>
                        <th>Time</th>
                        <th>Status</th>
                    </tr>
                </thead>    
                    <tbody>
                        {
                            this.state.tasks.map((name,index) =>
                             <TodoList stt={index} name={name.detail} time={name.time} status={name.complete}/>
                            )
                        }
                    </tbody>
                </Table>
                <button type="submit" style={{marginRight: 5+ 'px'}} className="btn btn-default" onClick={this.setStatus}>Add Work</button>
                <button type="submit" style={{marginRight: 5+ 'px'}} className="btn btn-default" >Delete Work</button>

            </div>
        )
        }
    }
}

export default TaskList;
